/// <reference types="react" />
interface UserObject {
    username: string;
    avatar_url: string;
    name: string;
}
interface LabelsObject {
    label: string;
    placeholder: string;
    submit: string;
}
declare type Props = {
    userObjects: UserObject[];
    onSubmitAction?: (v: {
        text: string;
        tagged: Array<any>;
    }) => void;
    labels: LabelsObject;
};
declare const TextBox: ({ userObjects, onSubmitAction, labels }: Props) => JSX.Element;
export default TextBox;
