/// <reference types="react" />
interface ThemeProps {
    COLORS?: Object;
    TYPO?: Object;
    SPACING?: Object;
    RADIUS?: Object;
}
interface LabelsObject {
    label: string;
    placeholder: string;
    submit: string;
}
interface UserObject {
    username: string;
    avatar_url: string;
    name: string;
}
declare const defalutProps: {
    labels: {
        label: string;
        placeholder: string;
        submit: string;
    };
    userObjects: {
        username: string;
        avatar_url: string;
        name: string;
    }[];
};
declare type Props = {
    themeOptions?: ThemeProps;
    onSubmit: (v: any) => void;
    labels?: LabelsObject;
    userObjects: UserObject[];
} & typeof defalutProps;
declare const CommentBox: {
    ({ userObjects, themeOptions, onSubmit, labels }: Props): JSX.Element;
    defaultProps: {
        labels: {
            label: string;
            placeholder: string;
            submit: string;
        };
        userObjects: {
            username: string;
            avatar_url: string;
            name: string;
        }[];
    };
};
export default CommentBox;
